$(document).ready(function(){$(".header__burger").click(function(e){$(".header__burger,.header__menu").toggleClass("active"),$("body").toggleClass("_lock")}),$(".header__link").click(function(e){$(".header__burger,.header__menu").removeClass("active"),$("body").removeClass("_lock")})});const anchors=document.querySelectorAll('.header__list a[href*="#"]');for(let r of anchors)r.addEventListener("click",e=>{e.preventDefault();e=r.getAttribute("href");document.querySelector(""+e).scrollIntoView({behavior:"smooth",block:"start"})});